import React, { memo } from "react";
import { networkData } from "./component/data/data";
import '/home/pranava/Desktop/network_visualiser/src/ MapChart.css'
import {
  ComposableMap,
  Geographies,
  Geography,
  Marker,
  Annotation
} from "react-simple-maps";

import { AnnotationLabel } from "react-annotation";

const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

// function annotFunction(e){
// const labels = networkData.map((data, key) => {
//     return (
//         <Annotation
//             key = {key}
//             subject={data.coordinate}
//             dx={0}
//             dy={0}
//             connectorProps={{
//             stroke: "#000000",
//             strokeWidth: 1,
//             strokeLinecap: "round"
//             }}
//         >
//                 <text x="-8" textAnchor="middle" alignmentBaseline="middle" fill="#000000">
//                     {"Data Usage: "+data.data}
//                 </text>

//         </Annotation>
//         );
//     });

//     return ({labels});
// };

const MapChart = ( { setTooltipContent }) => {
  return (
      <ComposableMap>
        <Geographies geography={geoUrl}>
        {({geographies}) => 
            geographies.map(geo => (
                <Geography 
                    key={geo.rsmKey} 
                    geography={geo}
                    fill="#DDD"

                />
            ))
        }
        </Geographies>


        {networkData.map((data, key) => {
          var radius;
          var color = "#e6f7ff";

            {
              if(data.data<100){
                radius = 7
              }else if(data.data>=100 && data.data<300){
                radius = 10
                color = "#80ccff";
              } else if(data.data>=300 && data.data<500){
                radius = 17
                color = "#99c2ff"

              }else if(data.data>500){
                radius = 20
                color = "#8080ff"
              }
            }

            return (
            <Marker 
              key={key} 
              coordinates={data.coordinate} 
              stroke="0077b3"
              onMouseEnter={() => {
                setTooltipContent("Data Usage: ");
              }}
              onMouseLeave={() => {
                setTooltipContent("");
              }}
              style={{
                default: {
                  fill: "#D6D6DA",
                  outline: "none"
                },
                hover: {
                  fill: "#F53",
                  outline: "none"
                },
                pressed: {
                  fill: "#E42",
                  outline: "none"
                }
              }}
              >           
                    <circle r={radius} fill={color}/>
                    {/* <text x="-8" textAnchor="middle" alignmentBaseline="middle" fill="#000000">
                    {"Data Usage: "+data.data}
                    </text> */}
            </Marker>
            );
        })}

        {networkData.map((data, key) => {
                return (
                    <Annotation
                        key = {key}
                        subject={data.coordinate}
                        dx={0}
                        dy={0}
                        connectorProps={{
                        stroke: "#000000",
                        strokeWidth: 1,
                        strokeLinecap: "round"
                        }}
                    >
                    <p textAnchor="middle">
                    {"Data Usage: "+data.data}
                    </p>



                    </Annotation>
                    );
        })} 
      </ComposableMap>
  );
};

export default memo(MapChart);
