import React from "react";
import { networkData } from "./component/data/data";
import {
  ComposableMap,
  Geographies,
  Geography,
  Marker,
  Annotation
} from "react-simple-maps";

import { AnnotationLabel } from "react-annotation";

const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

const MapChart = () => {

    function annotFunction(e){
        return (
            networkData.map((data, key) => {
                return (
                    <Annotation
                        subject={data.coordinate}
                        dx={0}
                        dy={0}
                        connectorProps={{
                        stroke: "#000000",
                        strokeWidth: 1,
                        strokeLinecap: "round"
                        }}
                    >
                            <text x="-8" textAnchor="middle" alignmentBaseline="middle" fill="#000000">
                                {"Data Usage: "+data.data}
                            </text>

                    </Annotation>
                );
            })
        );
    }
  return (
      <ComposableMap>
        <Geographies geography={geoUrl}>
        {({geographies}) => 
            geographies.map(geo => (
                <Geography 
                    key={geo.rsmKey} 
                    geography={geo}
                    fill="#DDD"

                />
            ))
        }
        </Geographies>
        {networkData.map((data, key) => {
            return (
            <Marker coordinates={data.coordinate} stroke="0077b3">

                    <circle r={8} fill="#00aaff" onClick={annotFunction}/>

            </Marker>
            );
        })}

        {networkData.map((data, key) => {
            return (
                <Annotation
                    subject={data.coordinate}
                    dx={0}
                    dy={0}
                    connectorProps={{
                    stroke: "#000000",
                    strokeWidth: 1,
                    strokeLinecap: "round"
                    }}
                >
                        <text x="-8" textAnchor="middle" alignmentBaseline="middle" fill="#000000">
                            {"Data Usage: "+data.data}
                        </text>

                </Annotation>
                // <AnnotationLabel
                //     x={data.coordinate[0]}
                //     y={data.coordinate[1]}
                //     dy={400}
                //     dx={500}
                //     color={"#9610ff"}
                //     className="show-bg" 
                //     editMode={true}
                //     note={{"title":"Annotations :)",
                //         "label":"Longer text to show text wrapping",
                //         "align":"middle",
                //         "orientation":"topBottom",
                //         "bgPadding":20,
                //         "padding":15,
                //         "titleColor":"#59039c"}}
                // />
            );
        })}


      </ComposableMap>
  );
};

export default MapChart;
