import React from "react";
import { networkData } from "./component/data/data";
import {
  ComposableMap,
  Geographies,
  Geography,
  Marker,
  Annotation
} from "react-simple-maps";

import { AnnotationLabel } from "react-annotation";

const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

function annotFunction(e){
const labels = networkData.map((data, key) => {
    return (
        <Annotation
            key = {key}
            subject={data.coordinate}
            dx={0}
            dy={0}
            connectorProps={{
            stroke: "#000000",
            strokeWidth: 1,
            strokeLinecap: "round"
            }}
        >
                <text x="-8" textAnchor="middle" alignmentBaseline="middle" fill="#000000">
                    {"Data Usage: "+data.data}
                </text>

        </Annotation>
        );
    });

    return ({labels});
};

const MapChart = () => {
  return (
      <ComposableMap>
        <Geographies geography={geoUrl}>
        {({geographies}) => 
            geographies.map(geo => (
                <Geography 
                    key={geo.rsmKey} 
                    geography={geo}
                    fill="#DDD"

                />
            ))
        }
        </Geographies>
        {networkData.map((data, key) => {
            return (
            <Marker key={key} coordinates={data.coordinate} stroke="0077b3" onClick={annotFunction}>

                    <circle r={8} fill="#00aaff"/>

            </Marker>
            );
        })}

        {networkData.map((data, key) => {
                return (
                    <Annotation
                        key = {key}
                        subject={data.coordinate}
                        dx={0}
                        dy={0}
                        connectorProps={{
                        stroke: "#000000",
                        strokeWidth: 1,
                        strokeLinecap: "round"
                        }}
                    >
                            <text x="-8" textAnchor="middle" alignmentBaseline="middle" fill="#000000">
                                {"Data Usage: "+data.data}
                            </text>

                    </Annotation>
                    );
                })}




      </ComposableMap>
  );
};

export default MapChart;
